"use strict";

function getConnectionDialect(connection) {
  return connection.options.dialect;
}

exports.isMySQL = function (connection) {
  return ['mysql', 'mariadb'].includes(getConnectionDialect(connection));
};

exports.isMSSQL = function (connection) {
  return getConnectionDialect(connection) === 'mssql';
};

exports.isSQLite = function (connection) {
  return getConnectionDialect(connection) === 'sqlite';
};